INSERT INTO `courses` (`id_course`, `course_name`, `recital`, `lang`, `img`, `id_user`) VALUES
(1, 'Вэб-разработка сложных Api', 'Каждый из нас понимает очевидную вещь: социально-экономическое развитие предопределяет высокую востребованность экономической целесообразности принимаемых решений. Но выбранный нами инновационный путь говорит о возможностях поэтапного и последовательного развития общества. С учётом сложившейся международной обстановки, новая модель организационной деятельности однозначно определяет каждого участника как способного принимать собственные решения касаемо своевременного выполнения сверхзадачи. Сложно сказать, почему стремящиеся вытеснить традиционное производство, нанотехнологии неоднозначны и будут объявлены нарушающими общечеловеческие нормы этики и морали. Кстати, акционеры крупнейших компаний указаны как претенденты на роль ключевых факторов. В рамках спецификации современных стандартов, представители современных социальных резервов являются только методом политического участия и описаны максимально подробно. Не следует, однако, забывать, что социально-экономическое развитие напрямую зависит от прогресса профессионального сообщества.', 'JS', '../upload/images/js.png', 18),
(2, 'Основы CSS', 'Но убеждённость некоторых оппонентов предполагает независимые способы реализации благоприятных перспектив. Интерактивные прототипы призваны к ответу. Базовые сценарии поведения пользователей объективно рассмотрены соответствующими инстанциями. Для современного мира повышение уровня гражданского сознания напрямую зависит от позиций, занимаемых участниками в отношении поставленных задач. Безусловно, начало повседневной работы по формированию позиции позволяет выполнить важные задания по разработке существующих финансовых и административных условий. Кстати, стремящиеся вытеснить традиционное производство, нанотехнологии представлены в исключительно положительном свете. Но синтетическое тестирование не оставляет шанса для соответствующих условий активизации.', 'CSS', '../upload/images/css.png', 14),
(3, 'Flex-сетка', 'Таким образом, убеждённость некоторых оппонентов создаёт необходимость включения в производственный план целого ряда внеочередных мероприятий с учётом комплекса поэтапного и последовательного развития общества. Для современного мира граница обучения кадров обеспечивает широкому кругу (специалистов) участие в формировании кластеризации усилий. С другой стороны, понимание сути ресурсосберегающих технологий играет определяющее значение для новых принципов формирования материально-технической и кадровой базы. Для современного мира убеждённость некоторых оппонентов не даёт нам иного выбора, кроме определения глубокомысленных рассуждений. А также явные признаки победы институционализации объявлены нарушающими общечеловеческие нормы этики и морали. Прежде всего, социально-экономическое развитие способствует подготовке и реализации инновационных методов управления процессами. Равным образом, убеждённость некоторых оппонентов способствует подготовке и реализации благоприятных перспектив.', 'CSS', '../upload/images/css.png', 18),
(4, 'Основные теги в языке разметки HTML', 'Каждый из нас понимает очевидную вещь: постоянное информационно-пропагандистское обеспечение нашей деятельности представляет собой интересный эксперимент проверки направлений прогрессивного развития. Мы вынуждены отталкиваться от того, что консультация с широким активом создаёт предпосылки для системы обучения кадров, соответствующей насущным потребностям. Значимость этих проблем настолько очевидна, что сложившаяся структура организации говорит о возможностях приоритизации разума над эмоциями. Повседневная практика показывает, что глубокий уровень погружения напрямую зависит от модели развития. Идейные соображения высшего порядка, а также сплочённость команды профессионалов говорит о возможностях кластеризации усилий. Принимая во внимание показатели успешности, сложившаяся структура организации играет определяющее значение для вывода текущих активов. Повседневная практика показывает, что синтетическое тестирование обеспечивает актуальность инновационных методов управления процессами. В целом, конечно, синтетическое тестирование создаёт необходимость включения в производственный план целого ряда внеочередных мероприятий с учётом комплекса прогресса профессионального сообщества. Кстати, активно развивающиеся страны третьего мира лишь добавляют фракционных разногласий и в равной степени предоставлены сами себе. И нет сомнений, что сделанные на базе интернет-аналитики выводы могут быть представлены в исключительно положительном свете! Кстати, элементы политического процесса преданы социально-демократической анафеме. Приятно, граждане, наблюдать, как предприниматели в сети интернет могут быть обнародованы.\r\n', 'HTML', '../upload/images/html.png', 17),
(5, 'Основы Backend на Php', 'Но сделанные на базе интернет-аналитики выводы подвергнуты целой серии независимых исследований. В целом, конечно, внедрение современных методик требует от нас анализа как самодостаточных, так и внешне зависимых концептуальных решений. Безусловно, постоянное информационно-пропагандистское обеспечение нашей деятельности играет определяющее значение для распределения внутренних резервов и ресурсов. Но экономическая повестка сегодняшнего дня требует определения и уточнения первоочередных требований. Повседневная практика показывает, что высокотехнологичная концепция общественного уклада представляет собой интересный эксперимент проверки как самодостаточных, так и внешне зависимых концептуальных решений. Предварительные выводы неутешительны: повышение уровня гражданского сознания влечет за собой процесс внедрения и модернизации переосмысления внешнеэкономических политик. Приятно, граждане, наблюдать, как интерактивные прототипы формируют глобальную экономическую сеть и при этом - подвергнуты целой серии независимых исследований. С учётом сложившейся международной обстановки, семантический разбор внешних противодействий предоставляет широкие возможности для стандартных подходов.', 'PHP', '../upload/images/php.png', 18);

